import BackToMenu from "../components/BackToMenu";
import OrderShowBar from "../components/OrderShowBar";
import BackGround from "../components/BackGround";
import SelectCoffee from "../components/SelectCoffee";
import {  } from "react";
import { createContext, useContext } from "react";
function SelectedCoffee() {
    // const coffeeInfo = {
    //     name:" ",
    //     price:0,
    //     num:0,
    //     size:" ",
    //     add:" ",
    //     milk:" "
    // }
    const [coffeeList, setCoffeeList] = useState([])

    function addCoffeeOrder(item) {
        setCoffeeList([...coffeeList, item])
    }

    const UserContext = createContext();

    function UserProvider({ children }) {
        const [user, setUser] = useState(null);

        return (
            <UserContext.Provider value={{ user, setUser }}>
                {children}
            </UserContext.Provider>
        );
    }

    function useUser() {
        const context = useContext(UserContext);
        if (!context) {
            throw new Error("useUser must be used within a UserProvider");
        }
        return context;
    }

    return (
        <>
            <BackToMenu />
            <OrderShowBar theCoffeeInfo={coffeeList} />
            <BackGround />
            <SelectCoffee theAddCoffeeOrder={addCoffeeOrder} />
        </>
    )
}
export default SelectedCoffee;
